const changeFoo = urlReplaceInAction('CHANGE_FOO', 'foo', UrlQueryParamTypes.number);
dispatch(changeFoo(137));
/*
dispatches action of form:
  {
    type: 'CHANGE_FOO',
    meta: {
      urlQuery: true,
      updateType: UrlUpdateTypes.replaceIn
    },
    payload: {
      queryParam: 'foo',
      encodedValue: '137',
